script:
if(Hls.isSupported())
{
    var video = document.getElementById('video');
    video.volume = 0.9;
    var hls = new Hls();
    hls.loadSource('https://cdn-livetv2.metube.id/hls/rcti_480/index.m3u8');
    hls.attachMedia(video);
    hls.on(Hls.Events.MANIFEST_PARSED,function()
    {
        video.play();
    });
}
else if (video.canPlayType('application/vnd.apple.mpegurl'))
{
    video.src = 'https://cdn-livetv2.metube.id/hls/rcti_480/index.m3u8';
    video.addEventListener('canplay',function()
    {
        video.play();
    });
}
